function setCookie(b, f, c){var e = new Date(); e.setTime(e.getTime() + (c * 24 * 60 * 60 * 1000)); var a = "expires=" + e.toUTCString(); document.cookie = b + "=" + f + ";" + a + ";path=/"}function getCookie(d){var b = d + "="; var f = decodeURIComponent(document.cookie); var a = f.split(";"); for (var e = 0; e < a.length; e++){var g = a[e]; while (g.charAt(0) == " "){g = g.substring(1)}if (g.indexOf(b) == 0){return g.substring(b.length, g.length)}}return false};